import * as fcl from "@onflow/fcl";
import { useEffect, useState } from "react";
import './App.css';

import { GetNFT } from "./cadence/scripts/getNFT";
import { TotalSupply } from "./cadence/scripts/getTotalSupply";
import { CreateNFT } from "./cadence/transactions/createNFT";

//0xf8d6e0586b0a20c7

function App()
{

  useEffect(() => { 
    try {
      fcl
        .config()
        .put("app.detail.title", "Basic NFT")
        .put(
          "app.detail.icon",
          "https://images.pexels.com/photos/1643457/pexels-photo-1643457.jpeg?auto=compress&cs=tinysrgb&w=1600"
        )
        .put("accessNode.api", "https://rest-testnet.onflow.org")
        .put("discovery.wallet", "https://fcl-discovery.onflow.org/testnet/authn");
    } catch (err) {
      console.log(err.message);
    }
  }, []);

  const [user, setUser] = useState();
  const catURL =
    "https://images.pexels.com/photos/1643457/pexels-photo-1643457.jpeg?auto=compress&cs=tinysrgb&w=1600";

  const login = () =>
  {
    try {
      fcl.authenticate();
      fcl.currentUser().subscribe(setUser);
    }
    catch (err) { console.log(err.message); }
  }

  const getNFT = async () =>
  {
    const result = await fcl
      .send([fcl.script(GetNFT)])
      .then(fcl.decode);

    console.log(result);
  };

  const getTotalSupply = async () =>
  {
    const result = await fcl.send([fcl.script(TotalSupply)]).then(fcl.decode);

    console.log(result);
  };

  const createNFT = async () =>
  {
    const txID = await fcl
      .send([
        fcl.transaction(CreateNFT),
        fcl.args([fcl.arg(catURL, fcl.t.String)]),
        fcl.payer(fcl.authz),
        fcl.proposer(fcl.authz),
        fcl.authorizations([fcl.authz]),
        fcl.limit(9999),
      ])
      .then(fcl.decode);

    console.log(txID);
  };

  return (
    <div className="App">
      <p>-------------------</p>
      <h1>Basic NFT</h1>
      <p>-------------------</p>
      <h2>Current Address : {user && user.addr ? user.addr : ""}</h2>
      <button onClick={login()}>Login</button>
      <p>-------------------</p>
      <h1>Create NFT</h1>
      <p>-------------------</p>
      <button onClick={createNFT()}>Create NFT</button>
      <p>-------------------</p>
      <h1>Get NFT</h1>
      <p>-------------------</p>
      <button onClick={getNFT()}>Get NFT</button>
      <p>-------------------</p>
      <h1>Get Total Supply</h1>
      <p>-------------------</p>
      <button onClick={getTotalSupply()}>Get Total Supply</button>
    </div>
  );
}

export default App;
